pragma solidity ^0.7.1;
pragma experimental ABIEncoderV2;

import '@nomiclabs/buidler/console.sol';
import './TokensFlow.sol';
import './ABDKMath64x64.sol';

contract BaseCarbon is TokensFlow
{
    using SafeMath for uint256;
    using ABDKMath64x64 for int128;

    address public globalCommunityFund;
    // uint256 public retiredCreditsToken; // M+ token
    // uint256 public nonRetiredCreditsToken;
    int128 public tax = int128(10).div(100); // 10%

// Admin

    constructor(address _globalCommunityFund,
                string memory _retiredName, string memory _retiredSymbol, string memory _retiredUri,
                string memory _nonRetiredName, string memory _nonRetiredSymbol, string memory _nonRetiredUri)
    {
        globalCommunityFund = _globalCommunityFund;
        _newToken(0, _retiredName, _retiredSymbol, _retiredUri, _globalCommunityFund); // 1
        _newToken(0, _nonRetiredName, _nonRetiredSymbol, _nonRetiredUri, _globalCommunityFund); // 2
    }

    function setGlobalCommunityFundAddress(address _globalCommunityFund) external {
        require(msg.sender == globalCommunityFund);
        require(_globalCommunityFund != address(0));
        globalCommunityFund = _globalCommunityFund;
    }

    function setTax(int128 _tax) external {
        require(msg.sender == globalCommunityFund);
        require(_tax >= 0 && _tax < 1<<64); // 0-100%
        tax = _tax;
    }

    // function setMainTokens(uint256 _retiredCreditsToken, uint256 _nonRetiredCreditsToken) external { // needed?
    //     require(msg.sender == globalCommunityFund);
    //     require(_retiredCreditsToken != 0 && _nonRetiredCreditsToken != 0);
    //     retiredCreditsToken = _retiredCreditsToken;
    //     nonRetiredCreditsToken = _nonRetiredCreditsToken;
    // }

// Credits

    // WARNING: If the caller of this function is a contract, it must implement ERC1155TokenReceiver interface.
    function retireCredit(uint _amount) external {
        _doBurn(msg.sender, _nonRetiredCreditsToken(), _amount);
        uint256 _taxAmount = uint256(tax.mulu(_amount));
        bytes memory _data = ""; // efficient?
        _doMint(globalCommunityFund, _retiredCreditsToken(), _taxAmount, _data);
        _doMint(msg.sender, _retiredCreditsToken(), _amount - _taxAmount, _data);
        // emit CreditRetired(creditId); // not needed as _doBurn() emits a suitable event
    }

// Internal

    function _retiredCreditsToken() pure internal returns(uint256) {
        return 1; // M+ token
    }

    function _nonRetiredCreditsToken() pure internal returns(uint256) {
        return 2;
    }

}
","keccak256":"0xee661780722e1743d2d347df8b6f162c5e2b743151bdaeac9d38e5431ed0ed75","license":"Apache-2.0"},"contracts/Carbon.sol":{"content":"//SPDX-License-Identifier: Apache-2.0	
pragma solidity ^0.7.1;
pragma experimental ABIEncoderV2;

// import '@nomiclabs/buidler/console.sol';
import './BaseCarbon.sol';

contract Carbon is BaseCarbon
{
    struct Authority {
        uint256 token;
        uint maxSerial;
    }

    struct CarbonCredit {
        address authority;
        uint serial;
        uint256 amount;
        address owner;
        bytes32 arweaveHash;
    }

    // token => Authority
    mapping (uint256 => Authority) public authorities;

    mapping (uint256 => CarbonCredit) public credits;

    uint256 maxCreditId;

    constructor(address _globalCommunityFund,
                string memory _retiredName, string memory _retiredSymbol, string memory _retiredUri,
                string memory _nonRetiredName, string memory _nonRetiredSymbol, string memory _nonRetiredUri)
        BaseCarbon(_globalCommunityFund,
                   _retiredName, _retiredSymbol, _retiredUri, _nonRetiredName, _nonRetiredSymbol, _nonRetiredUri)
    { }

    // Anybody can create an authority, but its parent decides if its tokens can be swapped.
    function createAuthority(uint256 _parent, string calldata _name, string calldata _symbol, string calldata _uri) external {
        // require(msg.sender == globalCommunityFund;
        // Minting restricted because minting can happen only through createCredit().
        uint256 _token = _newToken(_parent, _name, _symbol, _uri, msg.sender);
        Authority memory _authority = Authority({maxSerial: 0, token: _token});
        authorities[_token] = _authority;
        emit AuthorityCreated(msg.sender, _token, _name, _symbol, _uri);
    }

    // WARNING: If `_owner` is a contract, it must implement ERC1155TokenReceiver interface.
    // Additional data (such as the list of signers) is provided in Arweave.
    function createCredit(uint256 _token, uint256 _amount, address _owner, bytes32 _arweaveHash) external returns(uint256) {
        require(tokenOwners[_token] == msg.sender && tokenFlow[_token].enabled);
        Authority storage _authority = authorities[_token];
        CarbonCredit memory _credit = CarbonCredit({authority: msg.sender,
                                                    serial: ++_authority.maxSerial,
                                                    amount: _amount,
                                                    owner: _owner,
                                                    arweaveHash: _arweaveHash});
        credits[++maxCreditId] = _credit;
        bytes memory _data = ""; // efficient?
        _doMint(_owner, _token, _amount, _data);
        emit CreditCreated(maxCreditId, msg.sender, _authority.maxSerial, _amount, _owner, _arweaveHash);
        return maxCreditId;
    }

    function _setTokenParent(uint256 _child, uint256 _parent) internal {
        // require(_child != 0 && _child <= maxTokenId); // not needed
        require(msg.sender == tokenOwners[_child]);

        _setTokenParentNoCheck(_child, _parent);
    }

    event AuthorityCreated(address owner, uint256 token, string name, string symbol, string uri);
    event CreditCreated(uint256 id, address authority, uint serial, uint256 amount, address owner, bytes32 arweaveHash);
}
